import React from "react";
import { useState } from "react";
import { Dialog } from "@mui/material";
import "./artist.css";
import { Link } from "react-router-dom";

export default function Artist({ image, bio, description, name }) {
  const [clicked, setclicked] = useState(false);


    return (<>
            <div className="page-container">
        <div
          onClick={() => {
            setclicked(!clicked);
          }}
        >
          <img className="img" src={image} alt="kalb" />
        </div>

        <h3>{name}</h3>

      </div>
      <div className="Dialog">
        <Dialog
          open={clicked}
          onClose={() => {
            setclicked(!clicked);
          }}
        >
          {clicked && (
            <div className="dialog">
              <div>
                <button className="xbtn" onClick={()=>{setclicked(!clicked)}}>x</button>
                <img className="iimg" src={image} alt="kalb" />
              </div>
              <h3 className="text">NAME:&ensp;{name}</h3>
             <h3 className="text">BIO:&ensp;{bio}</h3>
              <h3 className="text">DESCRIPTION:&ensp;{description}</h3>
             <div> <Link to="/artworks">
                <button className="navbtn">go to arts</button>
              </Link></div>
            </div>
          )}
        </Dialog>
      </div>
    </>
  );
}
