import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";


export default function ArtworkPost() {
  const [title, settitle] = useState("");
  const [price, setprice] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
const navigate=useNavigate();

  const handleSubmit = async () => {
      const data = new FormData();
      data.append("arttitle", title);
      data.append("artprice",price);
      data.append("artdesc",description)
      data.append("artimage", image);

       axios.post("http://localhost:5000/arts",data)
       .catch(err=>console.log(err));
       navigate("/Dashboard/artworks");
  };


  return (
    <div className="main">
      {image && (
        <img className="postImg" src={URL.createObjectURL(image)} alt="imagess" />
      )}
      <form onSubmit={handleSubmit}>

        <div className="cont">
          <label htmlFor="fileInput">
            <i className="writeIcon fas fa-plus">file</i>
          </label>
          <input name="file"type="file"id="fileInput"style={{display:"none"}} onChange={(e)=>{setImage(e.target.files[0])}}/>
          <input
          required
            name="name"
            type="text"
            placeholder="name"
            className="name"
            autoFocus={true}
            onChange={(e) => {
              settitle(e.target.value);
            }}
          />
          <textarea
          rows={4}
                    required
            name="bio"
            type="text"
            placeholder="bio"
            className="bio"
            autoFocus={true}
            onChange={(e) => {
              setprice(e.target.value);
            }}
          />
          <textarea
                    rows={4}

                    required
            name="description"
            type="text"
            placeholder="description"
            className="description"
            autoFocus={true}
            onChange={(e) => {
              setDescription(e.target.value);
            }}
          />
          <button className="submitbutton" type="submit">
            submit
          </button>
        </div>
      </form>
    </div>
  );
}
